package com.syntax.class07;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i=1; i<=5; i++) 
		{
			System.out.println("hello"); 
			
		}
		
		//print numbers from 1 to 20 
		for (int i=1; i<=20; i++) {
			System.out.print(i+" ");
		}
		
		//print numbers from 20 to 10 
				for (int f=20; f>=10; f--) {
					System.out.print(f+" ");
				}
		
	}

}
