package com.syntax.class08;

public class NestedLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=1; i<4; i++) { // outer loop
			
			System.out.println("hello");
			if(i==2)
			{continue;	}

	                                   //inner loop
			for(int j=1; j<=2; j++) {
				
				System.out.println("bye");
				
	}
			
	}	
			
	}
		
		
		

}